import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {



  @Input()
  public characterList: Character[] = [{
    name: 'Trunks',
    power: 10
  }]

  @Output()
  public onDelete: EventEmitter<number>= new EventEmitter();

  onDeleteCharacter(index:number):void{
    this.onDelete.emit(index);
  }

  @Output()
  public deleteCharacter: EventEmitter<string> = new EventEmitter();

  deleteCharacterById(id?:string):void{
    if (!id) return;
    this.deleteCharacter.emit(id);
  }


}
<h4>Listado</h4>
<ul class="list-group" >
  <li *ngFor="
  let character of characterList;
   let i = index;
    let isFirst = first;
    let isLast = last;
    let isEven = even;
    let isOdd = odd;"
  class="list-group-item d-flex justify-content-between align-items-center"
  [ngClass]="{'list-group-item-dark': isLast,
  'list-group-item-info': isEven}" >
  <div >
    <span class="text-primary">{{i+1}}. </span>
    <span>{{character.name}} - </span>
    <strong>Power: </strong>
    <span>{{character.power}}</span>
  </div>
     <!-- <button (click)="onDeleteCharacter(i)" class="btn btn-danger">⛔</button> -->
     <button (click)="deleteCharacterById(character.id!)" class="btn btn-danger">⛔</button>
  </li>
</ul>
