import { Component } from "@angular/core";

@Component({
  selector: 'app-counter',
  styleUrls: ['.../app.component.css'],
  template: `
  <h3>Counter: {{counter}}</h3>
  <button (click)="increaseBy(+1)" class="button">Click +1</button>
  <button (click)="increaseBy(-1)" class="button buttonrest">Click -1</button>
  <button (click)="resetValue()" class="button buttonreset">Reset</button>
  `,

})
export class CounterComponent{
  public counter:number = 0;

  increaseBy(value:number):void{
    this.counter +=value;
  }
  resetValue():void{
    this.counter = 0;
  }
}
