import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {
  public name: string = 'ironman';
  public age: number = 45;

  get capitalizedName():string{
    return this.name.toUpperCase();
  }

  getHeroDescription():string{
    return `${this.name} - ${this.age}`;
  }

  // changeHero():string{
  //   return this.name = 'spiderman';
  // }

  // changeAge():number{
  //   return this.age = 47;
  // }

  changeHero():void{
    this.name = 'spiderman';
  }

  changeAge():void{
    this.age = 47;
  }
  resetForm():void{
    this.name = 'ironman';
    this.age = 45;
  }
}
<h1>{{name}}</h1>
<dl>
  <td>Nombre:</td>
  <dd>{{name}}</dd>

  <td>Edad:</td>
  <dd>{{age}}</dd>

  <td>Método:</td>
  <dd>{{ getHeroDescription() }}</dd>

  <td>Capitalizado:</td>
  <dd>{{ capitalizedName }}</dd>

  <button
  *ngIf="name !== 'spiderman'"
  (click) = "changeHero()" Class = "btn btn-primary mx-2">Cambiar nombre</button>

  <button *ngIf="age !== 47" (click) = "changeAge()" class = "btn btn-primary">Cambiar edad</button>

  <button *ngIf="age !== 45 && name !=='ironman'" (click) = "resetForm()" class = "btn btn-primary mx-2">Reset</button>
</dl>
