import { Component } from "@angular/core";
import { Character } from '../interfaces/character.interface';
import { DbzService} from "../services/dbz.service";

@Component({
    selector: 'app-dbz-main-page',
    templateUrl: './main-page.component.html'
})

export class MainPageComponent {

  constructor(private dbzService: DbzService){
  }

  get character(): Character[]{
    return [...this.dbzService.characters];
  }
}
<h1>DBZ Personajes</h1>
<hr>

<div class="row">
    <div class="col">
      <dbz-list [characterList]="dbzService.characters" (deleteCharacter)="dbzService.deleteCharacterById($event)"></dbz-list>
    </div>
    <div class="col">
      <dbz-add-character (onNewCharacter)="dbzService.onNewCharacter($event)"></dbz-add-character>
    </div>
</div>
